import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MAX_FILE_SIZE = 8 * 1024 * 1024; // 8 MB

function App() {
    const [file, setFile]                 = useState(null);
    const [fileError, setFileError]       = useState('');
    const [summary, setSummary]           = useState('');
    const [history, setHistory]           = useState([]);
    const [isProcessing, setIsProcessing] = useState(false);
    const [copySuccess, setCopySuccess]   = useState(false);

    // Load history from localStorage on mount
    useEffect(() => {
        const saved = localStorage.getItem('summaryHistory');
        if (saved) {
            setHistory(JSON.parse(saved).reverse());
        }
    }, []);

    // Handle file selection and size check
    const handleFileChange = (e) => {
        const selected = e.target.files[0];
        if (!selected) return;

        if (selected.size > MAX_FILE_SIZE) {
            setFile(null);
            setSummary('');
            setFileError('File is too large (maximum 8 MB)');
        } else {
            setFile(selected);
            setSummary('');
            setFileError('');
        }
    };

    // Upload PDF and get summary
    const handleUpload = async () => {
        if (!file) {
            alert('Please select a PDF file before uploading.');
            return;
        }

        setIsProcessing(true);

        const formData = new FormData();
        formData.append('pdf', file);

        try {
            const response = await axios.post(
                'http://localhost:5000/upload',
                formData,
                { headers: { 'Content-Type': 'multipart/form-data' } }
            );

            const newSummary = response.data.summary;
            setSummary(newSummary);

            // Update history (keep last 5)
            const entry = { fileName: file.name, summary: newSummary };
            const updated = [entry, ...history].slice(0, 5);
            setHistory(updated);
            localStorage.setItem('summaryHistory', JSON.stringify(updated.reverse()));
        } catch (err) {
            if (err.response?.status === 413) {
                alert('Server rejected file: size exceeds 8 MB');
            } else {
                console.error(err);
                alert('An error occurred while uploading or processing the file.');
            }
        } finally {
            setIsProcessing(false);
            setFile(null);
        }
    };

    // Copy text to clipboard
    const handleCopy = (text) => {
        navigator.clipboard.writeText(text)
            .then(() => {
                setCopySuccess(true);
                setTimeout(() => setCopySuccess(false), 1500);
            })
            .catch((e) => {
                console.error('Copy failed', e);
                alert('Failed to copy to clipboard.');
            });
    };

    // Delete a history item
    const handleDelete = (idx) => {
        const filtered = history.filter((_, i) => i !== idx);
        setHistory(filtered);
        localStorage.setItem('summaryHistory', JSON.stringify(filtered.reverse()));
    };

    return (
        <div className="min-h-screen bg-gray-100 flex items-center justify-center py-12 px-4">
            <div className="max-w-xl w-full">
                <h1 className="text-4xl font-bold text-center mb-4">PDF Summary AI</h1>
                <p className="text-center text-gray-600 mb-8">
                    Upload a PDF to generate an AI‑powered summary.
                </p>

                <div className="mb-6">
                    <input
                        type="file"
                        accept="application/pdf"
                        onChange={handleFileChange}
                        className="block w-full text-sm text-gray-900 bg-white border border-gray-300 rounded-lg cursor-pointer focus:outline-none"
                    />
                    {fileError && (
                        <p className="mt-2 text-sm text-red-600">{fileError}</p>
                    )}

                    <div className="mt-4 text-center">
                        {!isProcessing && file && !fileError && (
                            <button
                                onClick={handleUpload}
                                className="px-6 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition"
                            >
                                Upload & Generate Summary
                            </button>
                        )}
                        {isProcessing && (
                            <p className="text-blue-600 font-medium">Processing…</p>
                        )}
                    </div>
                </div>

                {summary && (
                    <div className="mb-8 bg-white p-4 rounded-lg shadow">
                        <h2 className="text-2xl font-semibold mb-2">Summary</h2>
                        <p className="text-gray-800">{summary}</p>
                    </div>
                )}

                {history.length > 0 && (
                    <div className="bg-white p-4 rounded-lg shadow">
                        <h2 className="text-2xl font-semibold mb-4">History</h2>
                        <ul className="space-y-4">
                            {history.map((item, idx) => (
                                <li key={idx} className="border p-3 rounded flex flex-col">
                  <span className="text-sm text-gray-500 mb-1">
                    {item.fileName}
                  </span>
                                    <p className="text-gray-800 mb-2">{item.summary}</p>
                                    <div className="mt-auto flex justify-end space-x-4">
                                        <button
                                            onClick={() => handleCopy(item.summary)}
                                            className="text-blue-600 hover:underline text-sm"
                                        >
                                            {copySuccess ? 'Copied!' : 'Copy'}
                                        </button>
                                        <button
                                            onClick={() => handleDelete(idx)}
                                            className="text-red-600 hover:underline text-sm"
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
